package com.open.learncode.算法.java.剑指offer.A08_位运算;

import com.open.learncode.算法.base.PrintUtils;

/**
 * 题目：
 * 两个大数两加，且两个数都是以字符串的方式提供。
 * <p>
 * 解题思路：
 * 分析10进制如何进位的然后模仿
 * <p>
 * 复杂度分析：
 * 时间复杂度：O(n)。
 * 空间复杂度：O(1)。
 */
public class OT_大数相加 {

    public static void main(String[] args) {
        // 测试用例
        PrintUtils.getInstance().print(solution(null, "111111"));
        PrintUtils.getInstance().print(solution("1111", null));
        PrintUtils.getInstance().print(solution(null, null));
        PrintUtils.getInstance().print(solution(""
                , ""));
    }

    private static String solution(String str1, String str2) {
        if (str1 == null || str1.length() <= 0) return str2;
        if (str2 == null || str2.length() <= 0) return str1;

        StringBuilder strb = new StringBuilder();
        int index1 = str1.length() - 1;
        int index2 = str2.length() - 1;
        int k = 0;
        while (index1 >= 0 || index2 >= 0) {
            int num1 = index1 >= 0 ? str1.charAt(index1) - '0' : 0;
            int num2 = index2 >= 0 ? str2.charAt(index2) - '0' : 0;
            int temp = num1 + num2 + k;
            k = temp / 10;
            strb.append(temp % 10);
            index1--;
            index2--;
        }
        if (k > 0) strb.append(k);
        return strb.reverse().toString();
    }
}
