package com.open.learncode.算法.java.剑指offer.A8_位运算;

import com.open.learncode.算法.base.PrintUtils;

/**
 * 题目：
 * 两个大数两加，且两个数都是以字符串的方式提供。
 * <p>
 * 解题思路：
 * 分析10进制如何进位的然后模仿
 * <p>
 * 复杂度分析：
 * 时间复杂度：O(n)，空间复杂度：O(1)
 */
public class OT_大数相加 {

    public static void main(String[] args) {
        PrintUtils.getInstance().print(method(null, "111111"));
        PrintUtils.getInstance().print(method("1111", null));
        PrintUtils.getInstance().print(method(null, null));
        PrintUtils.getInstance().print(method(""
                , ""));
    }


    /**
     * 大数相加
     *
     * @param num1 大数
     * @param num2 大数
     * @return 返回两个大数相加的和
     */
    public static String method(String num1, String num2) {
        if (num1 == null) return num2;
        if (num2 == null) return num1;
        StringBuilder strb = new StringBuilder();
        int i = num1.length() - 1;
        int j = num2.length() - 1;
        int k = 0;
        while (i >= 0 || j >= 0) {
            int n1 = i >= 0 ? num1.charAt(i) - '0' : 0;
            int n2 = j >= 0 ? num2.charAt(j) - '0' : 0;
            int temp = n1 + n2 + k;
            k = temp / 10;
            strb.append(temp % 10);
            i--;
            j--;
        }
        if (k > 0) strb.append(k);
        return strb.reverse().toString();
    }
}
